---
import type { HTMLAttributes } from "astro/types"

import { cn } from "@/lib/utils"

interface Props extends HTMLAttributes<"div"> {}

const { class: className, ...props } = Astro.props

const slot = await Astro.slots.render("default")
---

{
  slot?.trim().length > 0 && (
    <div data-slot="accordion" class={cn("w-full", className)} {...props}>
      <Fragment set:html={slot} />
    </div>
  )
}

---
import type { HTMLAttributes } from "astro/types"

import { cn } from "@/lib/utils"

interface Props extends HTMLAttributes<"div"> {}

const { class: className, ...props } = Astro.props

const slot = await Astro.slots.render("default")
---

{
  slot?.trim().length > 0 && (
    <div
      data-slot="accordion-content"
      class={cn(
        "text-sm pt-0 pb-4",
        "grid grid-rows-[0fr] transition-[grid-template-rows] duration-200 ease-out group-open:grid-rows-[1fr]",
        className
      )}
      {...props}
    >
      <div class="overflow-hidden">
        <Fragment set:html={slot} />
      </div>
    </div>
  )
}

---
import type { HTMLAttributes } from "astro/types"

import { cn } from "@/lib/utils"

interface Props extends Omit<HTMLAttributes<"details">, "class"> {
  class?: string
  open?: boolean
}

const { class: className, open = false, ...props } = Astro.props

const slot = await Astro.slots.render("default")
---

{
  slot?.trim().length > 0 && (
    <details
      data-slot="accordion-item"
      class={cn("border-b last:border-b-0", "group", className)}
      open={open}
      {...props}
    >
      <Fragment set:html={slot} />
    </details>
  )
}

---
import type { HTMLAttributes } from "astro/types"
import ChevronDownIcon from "lucide-static/icons/chevron-down.svg"

import { cn } from "@/lib/utils"

interface Props extends Omit<HTMLAttributes<"summary">, "class"> {
  class?: string
}

const { class: className, ...props } = Astro.props

const slot = await Astro.slots.render("default")
---

{
  slot?.trim().length > 0 && (
    <summary
      data-slot="accordion-trigger"
      class={cn(
        "focus-visible:border-ring focus-visible:ring-ring/50 flex flex-1 items-start justify-between gap-4 rounded-md py-4 text-left text-sm font-medium transition-all outline-none hover:underline focus-visible:ring-[3px] disabled:pointer-events-none disabled:opacity-50",
        "cursor-pointer list-none [&::-webkit-details-marker]:hidden group-open:[&>svg]:rotate-180",
        className
      )}
      {...props}
    >
      <span class="flex-1">
        <Fragment set:html={slot} />
      </span>
      <ChevronDownIcon class="text-muted-foreground pointer-events-none size-4 shrink-0 translate-y-0.5 transition-transform duration-200" />
    </summary>
  )
}
