---
import { Accordion as AccordionComposite } from '@/components/composite/accordion'
import type { ComponentProps } from 'astro/types'

interface Props extends ComponentProps<typeof AccordionComposite> {}

const { items, ...rest } = Astro.props
---

{
  items && (
    <AccordionComposite
      client:load
      {items}
      {...rest}
    />
  )
}

import {
  AccordionContent,
  AccordionItem,
  Accordion as AccordionRoot,
  AccordionTrigger,
} from '@/components/ui/accordion'

interface Props {
  items?:
    | {
        title?: React.ComponentProps<typeof Title>[

] | null
        description?: string | null
      }[]
    | null
}

export function Accordion({ items }: Props) {
  return (
    <AccordionRoot
      type="single"
      collapsible
      className="w-full max-w-xl"
    >
      {items?.map((item, index) => (
        <AccordionItem
          value={`item-${index}`}
          key={`item-${index}`}
        >
          <AccordionTrigger>{item.title}</AccordionTrigger>
          <AccordionContent>{item.description}</AccordionContent>
        </AccordionItem>
      ))}
    </AccordionRoot>
  )
}
