---
import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"
import { Icon } from "@/components/ui/icon"
import { Image } from "@/components/ui/image"
import { List, ListItem } from "@/components/ui/list"
import {
  Section,
  SectionActions,
  SectionContent,
  SectionMedia,
  SectionProse,
  SectionSplit,
} from "@/components/ui/section"

interface Props {
  class?: string
  id?: string
  list?: string[]
  links?: {
    icon?: string
    text?: string
    href?: string
    target?: string
  }[]
  image?: {
    src: string
    alt: string
  }
}

const { class: className, id, list, links, image } = Astro.props
---

<Section
  class={cn(
    "border-primary/20 my-0 -mt-px rounded-none shadow-none",
    className
  )}
  id={id}
  variant="floating"
>
  <SectionSplit class="@5xl:items-center">
    <SectionContent class="max-w-xl">
      <SectionProse>
        <slot />
      </SectionProse>
      <List>
        {
          list?.map((item) => (
            <ListItem>
              <Icon name="check" />
              {item}
            </ListItem>
          ))
        }
      </List>
      <SectionActions>
        {
          links?.map(({ icon, text, ...link }, i) => (
            <Button variant={i === 0 ? "outline" : "ghost"} {...link}>
              {icon && <Icon name={icon} />}
              {text}
            </Button>
          ))
        }
      </SectionActions>
    </SectionContent>
    <SectionMedia class="@5xl:order-first">
      <Image
        sizes="(min-width: 1536px) 768px, (min-width: 1024px) 50vw, 100vw"
        {...image}
      />
    </SectionMedia>
  </SectionSplit>
</Section>
