---
import { cn } from "@/lib/utils"
import { Avatar, AvatarImage } from "@/components/ui/avatar"
import { Button } from "@/components/ui/button"
import { Icon } from "@/components/ui/icon"
import {
  Item,
  ItemContent,
  ItemDescription,
  ItemMedia,
} from "@/components/ui/item"
import { Rating } from "@/components/ui/rating"
import {
  Section,
  SectionActions,
  SectionContent,
  SectionProse,
} from "@/components/ui/section"

interface Props {
  class?: string
  id?: string
  links?: {
    icon?: string
    text?: string
    href?: string
    target?: string
  }[]
  item?: {
    images?: {
      src: string
      alt: string
    }[]
    rating?: number
    description?: string
  }
}

const { class: className, id, links, item } = Astro.props
---

<Section
  class={cn(
    "border-primary/20 my-0 -mt-px rounded-none shadow-none",
    className
  )}
  id={id}
  variant="floating"
  size="lg"
>
  <SectionContent
    class={cn(
      "before:from-primary/20 relative items-center before:absolute before:top-full before:left-0 before:-z-10 before:h-[200%] before:w-full before:animate-pulse before:rounded-full before:bg-gradient-to-b before:to-transparent before:blur-3xl"
    )}
  >
    <Item class="p-0">
      <ItemMedia class="-space-x-5">
        {
          item?.images?.map((image) => (
            <Avatar class="ring-background size-11 ring">
              <AvatarImage {...image} />
            </Avatar>
          ))
        }
      </ItemMedia>
      <ItemContent class="mt-1">
        <Rating rating={item?.rating} />
        <ItemDescription>
          {item?.description}
        </ItemDescription>
      </ItemContent>
    </Item>
    <SectionProse class="text-center" size="lg">
      <slot />
    </SectionProse>
    <SectionActions class="justify-center">
      {
        links?.map(({ icon, text, ...link }, i) => (
          <Button
            variant={i === 0 ? "default" : "outline"}
            size="lg"
            {...link}
          >
            {icon && <Icon name={icon} />}
            {text}
          </Button>
        ))
      }
    </SectionActions>
  </SectionContent>
</Section>
