---
import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"
import { Icon } from "@/components/ui/icon"
import {
  Section,
  SectionActions,
  SectionContent,
  SectionGrid,
  SectionProse,
} from "@/components/ui/section"
import {
  Tile,
  TileActions,
  TileContent,
  TileDescription,
  TileMedia,
  TileTitle,
} from "@/components/ui/tile"

interface Props {
  class?: string
  id?: string
  links?: {
    icon?: string
    text?: string
    href?: string
    target?: string
  }[]
  items?: {
    title?: string
    description?: string
    icon?: string
    href?: string
    links?: {
      icon?: string
      text?: string
      href?: string
      target?: string
    }[]
  }[]
}

const { class: className, id, links, items } = Astro.props
---

<Section
  class={cn(
    "border-primary/20 my-0 -mt-px rounded-none shadow-none",
    className
  )}
  id={id}
  variant="floating"
>
  <SectionContent class="items-center">
    <SectionProse class="text-center">
      <slot />
    </SectionProse>
    <SectionActions class="justify-center">
      {
        links?.map(({ icon, text, ...link }, i) => (
          <Button variant={i === 0 ? "default" : "outline"} {...link}>
            {icon && <Icon name={icon} />}
            {text}
          </Button>
        ))
      }
    </SectionActions>
  </SectionContent>
  <SectionGrid>
    {
      items?.map(({ title, description, icon, links }) => (
        <Tile class="items-center">
          <TileMedia class="bg-primary/5 border-primary/20" variant="icon">
            <Icon class="text-primary" name={icon} />
          </TileMedia>
          <TileContent class="items-center text-center text-balance">
            <TileTitle>{title}</TileTitle>
            <TileDescription class="text-foreground">
              {description}
            </TileDescription>
          </TileContent>
          <TileActions class="justify-center">
            {links?.map(({ icon, text, href, target }) => (
              <Button
                class="h-auto p-0"
                variant="link"
                href={href}
                target={target}
              >
                {icon && <Icon name={icon} />}
                {text}
              </Button>
            ))}
          </TileActions>
        </Tile>
      ))
    }
  </SectionGrid>
</Section>
