---
import type { HTMLAttributes } from "astro/types"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

interface Props
  extends HTMLAttributes<"footer">,
    VariantProps<typeof variants> {}

const variants = cva(
  "relative mx-auto flex scroll-m-(--footer-py) flex-col gap-16 px-(--footer-px) py-(--footer-py)",
  {
    variants: {
      variant: {
        default: "bg-background w-full",
        floating: [
          "bg-background rounded-lg border shadow-sm",
          "w-[calc(100%-2*var(--gutter,24px))] max-w-(--footer-width)",
          "my-(--footer-py) overflow-hidden rounded-xl first:mt-2",
        ],
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
)

const { class: className, variant, ...props } = Astro.props

const slot = await Astro.slots.render("default")
---

{
  slot?.trim().length > 0 && (
    <footer
      class={cn(variants({ variant }), className)}
      data-slot="footer"
      data-variant={variant}
      {...props}
    >
      <Fragment set:html={slot} />
    </footer>
  )
}

<style is:global>
  [data-slot="footer"] {
    --footer-width: var(--container, var(--breakpoint-xl));
    --footer-py: calc(var(--spacing) * 12);
    --footer-px: max(
      var(--gutter, 24px),
      calc((100cqw - var(--footer-width)) / 2)
    );
  }

  [data-slot="footer"][data-variant="floating"] {
    --footer-px: calc(var(--spacing) * 6);
  }

  @media (min-width: 1024px) {
    [data-slot="footer"] {
      --footer-py: calc(var(--spacing) * 16);
    }

    [data-slot="footer"][data-variant="floating"] {
      --footer-px: calc(var(--spacing) * 16);
    }
  }
</style>

