---
import type { HTMLAttributes } from "astro/types"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

interface Props
  extends HTMLAttributes<"header">,
    VariantProps<typeof variants> {}

const variants = cva(
  "relative top-0 z-50 mx-auto flex h-(--header-height) items-center justify-between gap-8 border-b border-transparent px-(--header-px) py-4",
  {
    variants: {
      variant: {
        default: "bg-background sticky w-full",
        floating: [
          "w-[calc(100%-2*var(--gutter,24px))] max-w-(--header-width)",
          "top-2 mt-2 h-[calc(var(--header-height)-var(--spacing)*2)] overflow-hidden rounded-xl",
          "bg-background border-border sticky border",
        ],
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
)

const { class: className, variant, ...props } = Astro.props

const slot = await Astro.slots.render("default")
---

{
  slot?.trim().length > 0 && (
    <header
      class={cn(variants({ variant }), className)}
      data-slot="header"
      data-variant={variant}
      {...props}
    >
      <Fragment set:html={slot} />
    </header>
  )
}

<style is:global>
  [data-slot="header"] {
    --header-width: var(--container, var(--breakpoint-xl));
    --header-px: max(
      var(--gutter, 24px),
      calc((100cqw - var(--header-width)) / 2)
    );
  }

  [data-slot="header"][data-variant="floating"] {
    --header-px: calc(var(--spacing) * 6);
  }

  :root:has([data-slot="header"]) {
    --header-height: calc(var(--spacing) * 16);
  }

  @media (min-width: 1024px) {
    [data-slot="header"][data-variant="floating"] {
      --header-px: calc(var(--spacing) * 16);
    }
  }
</style>

