---
import type { HTMLAttributes } from "astro/types"

import { cn } from "@/lib/utils"

type Props = HTMLAttributes<"div"> & HTMLAttributes<"a">

const { class: className, href, ...props } = Astro.props

const Comp = href ? "a" : "div"

const slot = await Astro.slots.render("default")
---

{
  slot?.trim().length > 0 && (
    <Comp
      class={cn(
        "flex items-center justify-start gap-2.5 text-base font-semibold whitespace-nowrap",
        className
      )}
      href={href}
      {...props}
    >
      <Fragment set:html={slot} />
    </Comp>
  )
}

---
import type { ComponentProps } from "astro/types"

import { cn } from "@/lib/utils"
import { Image } from "@/components/ui/image"

type props = ComponentProps<typeof Image>

const { class: className, src, alt = "", ...props } = Astro.props
---

{
  src && (
    <Image
      class={cn("h-8 w-auto max-w-[200px] shrink-0 object-contain", className)}
      data-slot="logo-image"
      src={src}
      alt={alt}
      sizes="200px"
      widths={[200]}
      {...props}
    />
  )
}

---
import type { HTMLAttributes } from "astro/types"

import { cn } from "@/lib/utils"

interface Props extends HTMLAttributes<"span"> {}

const { class: className, ...props } = Astro.props

const slot = await Astro.slots.render("default")
---

{
  slot?.trim().length > 0 && (
    <span class={cn("text-lg", className)} {...props}>
      <Fragment set:html={slot} />
    </span>
  )
}
