---
import { getCollection, getEntry, render } from "astro:content"

import Layout from "@/components/layout.astro"

export async function getStaticPaths() {
  const pages = await getCollection("pages")
  return pages.map((page) => {
    return {
      params: {
        page: page.id === "index" ? undefined : page.id,
      },
      props: page,
    }
  })
}

const page = Astro.props
const { layout } = page.data
const { Content } = await render(Astro.props)

const indexLayout = await getEntry("layouts", "index")
const pageLayout = layout ? await getEntry("layouts", layout) : undefined
---

<Layout
  {...indexLayout?.data}
  {...pageLayout?.data}
  {...page.data}
  sections={[
    ...(page.data?.sections || []),
    ...(pageLayout?.data?.sections || []),
    ...(indexLayout?.data?.sections || []),
  ]}
>
  <Content />
</Layout>
