---
import type { HTMLAttributes } from "astro/types"

import { cn } from "@/lib/utils"

interface Props extends HTMLAttributes<"div"> {}

const { class: className, ...props } = Astro.props

const slot = await Astro.slots.render("default")
---

{
  slot?.trim().length > 0 && (
    <div
      class={cn("flex flex-wrap items-center gap-x-2", className)}
      {...props}
    >
      <Fragment set:html={slot} />
    </div>
  )
}

---
import type { HTMLAttributes } from "astro/types"

import { cn } from "@/lib/utils"

interface Props extends HTMLAttributes<"span"> {}

const { class: className, ...props } = Astro.props

const slot = await Astro.slots.render("default")
---

{
  slot?.trim().length > 0 && (
    <span
      class={cn("text-muted-foreground text-sm leading-tight", className)}
      {...props}
    >
      / <Fragment set:html={slot} />
    </span>
  )
}

---
import type { HTMLAttributes } from "astro/types"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

interface Props extends VariantProps<typeof variants>, HTMLAttributes<"span"> {
  price?: number | string
  currency?: string
  locale?: string
  variant?: "default" | "sale"
}

const variants = cva("leading-tight", {
  variants: {
    variant: {
      default: "text-foreground",
      sale: "text-muted-foreground line-through",
    },
  },
})

const {
  class: className,
  price,
  currency = "USD",
  locale = "en-US",
  variant = "default",
  ...props
} = Astro.props

// Format price with currency only if it's a number
const formattedPrice =
  typeof price === "number"
    ? new Intl.NumberFormat(locale, {
        style: "currency",
        currency: currency,
      }).format(price)
    : price
---

{
  price && (
    <span class={cn(variants({ variant }), className)} {...props}>
      {formattedPrice}
    </span>
  )
}
