---
import { Button } from "@/components/ui/button"
import { Icon } from "@/components/ui/icon"
import { Image } from "@/components/ui/image"
import { List, ListItem } from "@/components/ui/list"
import {
  NativeCarousel,
  NativeCarouselContent,
  NativeCarouselItem,
  NativeCarouselNext,
  NativeCarouselPrevious,
} from "@/components/ui/native-carousel"
import {
  Section,
  SectionActions,
  SectionContent,
  SectionProse,
  SectionSplit,
} from "@/components/ui/section"

interface Props {
  class?: string
  id?: string
  title?: string
  description?: string
  list?: string[]
  links?: {
    icon?: string
    text?: string
    href?: string
    target?: string
  }[]
  image?: {
    src: string
    alt: string
  }
  images?: {
    src: string
    alt: string
  }[]
}

const {
  class: className,
  id,
  title,
  description,
  list,
  links,
  images,
} = Astro.props
---

<Section class={className} id={id}>
  <SectionSplit class="items-start @5xl:gap-24">
    <NativeCarousel>
      <NativeCarouselContent>
        {
          images?.map((image) => (
            <NativeCarouselItem>
              <Image
                class="object-contain"
                sizes="(min-width: 1536px) 768px, (min-width: 1024px) 50vw, 100vw"
                {...image}
              />
            </NativeCarouselItem>
          ))
        }
      </NativeCarouselContent>
      <NativeCarouselPrevious class="left-6" />
      <NativeCarouselNext class="right-6" />
    </NativeCarousel>
    <SectionContent>
      <SectionProse>
        {title && <h1>{title}</h1>}
        {description && <p>{description}</p>}
      </SectionProse>
      <SectionActions>
        {
          links?.map(({ icon, text, ...link }, i) => (
            <Button variant={i === 0 ? "default" : "outline"} {...link}>
              {icon && <Icon name={icon} />}
              {text}
            </Button>
          ))
        }
      </SectionActions>
      <List>
        {
          list?.map((item) => (
            <ListItem>
              <Icon name="check" />
              {item}
            </ListItem>
          ))
        }
      </List>
      <SectionProse>
        <slot />
      </SectionProse>
    </SectionContent>
  </SectionSplit>
</Section>
