---
import type { HTMLAttributes } from "astro/types"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

interface Props
  extends HTMLAttributes<"section">,
    VariantProps<typeof variants> {
  size?: "sm" | "default" | "lg"
}

const variants = cva(
  "relative mx-auto flex scroll-m-(--section-py) flex-col gap-16 px-(--section-px) py-(--section-py)",
  {
    variants: {
      variant: {
        default: "bg-background w-full",
        floating: [
          "bg-background rounded-lg border shadow-md",
          "w-[calc(100%-2*var(--gutter,24px))] max-w-(--section-width)",
          "my-(--section-py) overflow-hidden rounded-xl",
        ],
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
)

const { class: className, variant, size, ...props } = Astro.props

const slot = await Astro.slots.render("default")
---

{
  slot?.trim().length > 0 && (
    <section
      class={cn(variants({ variant }), className)}
      data-slot="section"
      data-variant={variant}
      data-size={size}
      {...props}
    >
      <Fragment set:html={slot} />
    </section>
  )
}

<style is:global>
  [data-slot="section"] {
    --section-width: var(--container, var(--breakpoint-xl));
    --section-py: calc(var(--spacing) * 12);
    --section-px: max(
      var(--gutter, 24px),
      calc((100cqw - var(--section-width)) / 2)
    );
  }

  [data-slot="section"][data-variant="floating"] {
    --section-px: calc(var(--spacing) * 6);
  }

  [data-slot="section"][data-size="sm"] {
    --section-py: calc(var(--spacing) * 8);
  }

  [data-slot="section"][data-size="lg"] {
    --section-py: calc(var(--spacing) * 24);
  }

  @media (min-width: 1024px) {
    [data-slot="section"] {
      --section-py: calc(var(--spacing) * 16);
    }

    [data-slot="section"][data-variant="floating"] {
      --section-px: calc(var(--spacing) * 16);
    }

    [data-slot="section"][data-size="sm"] {
      --section-py: calc(var(--spacing) * 12);
    }

    [data-slot="section"][data-size="lg"] {
      --section-py: calc(var(--spacing) * 32);
    }
  }
</style>

