---
import type { HTMLAttributes } from "astro/types"

import { cn } from "@/lib/utils"

interface Props extends HTMLAttributes<"ul"> {}

const { class: className, ...props } = Astro.props

const slot = await Astro.slots.render("default")
---

{
  slot?.trim().length > 0 && (
    <ul
      data-slot="sidebar-menu"
      class={cn(
        "text-foreground flex w-full min-w-0 flex-col gap-1",
        className
      )}
      {...props}
    >
      <Fragment set:html={slot} />
    </ul>
  )
}

---
import type { HTMLAttributes } from "astro/types"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const variants = cva(
  "hover:bg-accent hover:text-accent-foreground focus-visible:outline-ring active:bg-accent active:text-accent-foreground flex w-full items-center gap-2 overflow-hidden rounded-md p-2 text-left text-sm outline-hidden transition-colors focus-visible:outline-2 focus-visible:outline-offset-2 disabled:pointer-events-none disabled:opacity-50 [&>span:last-child]:truncate [&>svg]:size-4 [&>svg]:shrink-0",
  {
    variants: {
      variant: {
        default: "hover:bg-accent hover:text-accent-foreground text-foreground",
        outline:
          "bg-background hover:bg-accent hover:text-accent-foreground shadow-[0_0_0_1px_hsl(var(--border))] hover:shadow-[0_0_0_1px_hsl(var(--accent))]",
      },
      size: {
        default: "h-8 text-sm",
        sm: "h-7 text-xs",
        lg: "h-12 text-sm",
      },
      active: {
        true: "bg-accent text-accent-foreground font-medium",
        false: "",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
      active: false,
    },
  }
)

type Props = VariantProps<typeof variants> &
  HTMLAttributes<"button"> &
  HTMLAttributes<"a"> & {
    href?: string
  }

const { class: className, variant, size, active, href, ...props } = Astro.props

const Comp = href ? "a" : "button"
const slot = await Astro.slots.render("default")
---

{
  slot?.trim().length > 0 && (
    <Comp
      data-slot="sidebar-menu-button"
      class={cn(variants({ variant, size, active }), className)}
      href={href}
      {...props}
    >
      <Fragment set:html={slot} />
    </Comp>
  )
}

